import java.util.*;class Jasio{public static void main(String[] args){int a=0;String b="ABCDEFGHIJKLMNOPQRSTUVWXYZ";Scanner s = new Scanner(System.in);int n=s.nextInt();for(int i=0;i<n;i++){for(int x=0;x<4;x++){a=s.nextInt();while(a<=0){a+=26;}a-=1;while(a>=26){a%=26;}System.out.print(b.charAt(a));}}}}